class Solution {
public:
    int dp[500001][4];

    int go(int pos,int lane,vector<int>& A){
        if(pos>=A.size())return 0;
        if(A[pos]==lane)return 1e8;

        if(dp[pos][lane]!=-1)
            return dp[pos][lane];

        long long int ans=go(pos+1,lane,A);
        for(int pos_lane=1;pos_lane<=3;pos_lane++){
            if(lane==pos_lane){
                continue;
            }
            else if(A[pos]!=pos_lane){
                ans=min(ans,(long long int)(1ll+(long long int)go(pos+1,pos_lane,A)));
            }
        }

        return dp[pos][lane]=ans;
    }
    int minSideJumps(vector<int>& obstacles) {
        memset(dp,-1,sizeof(dp));
        return go(0,2,obstacles);
    }
};
