class Solution:
    def minSideJumps(self, obstacles: List[int]) -> int:    

        n = len(obstacles)
        inf = sys.maxsize

        lane1,lane2,lane3 = 1,0,1

        for indx in range(1,n):
            lane1_yes,lane2_yes,lane3_yes = True,True,True
            if obstacles[indx] == 1:
                lane1 = inf
                lane1_yes = False
            elif obstacles[indx] == 2:
                lane2 = inf
                lane2_yes = False
            elif obstacles[indx] == 3:
                lane3 = inf
                lane3_yes = False


            if lane1_yes:
                lane1 = min(lane1,min(lane2+1,lane3+1))
            if lane2_yes:
                lane2 = min(lane2,min(lane1+1,lane3+1))
            if lane3_yes:
                lane3 = min(lane3,min(lane1+1,lane2+1))
        return min(lane3,min(lane1,lane2))




