we will continue to travel in the current lane until we find the stone
when we find a stone we will calculate the distance of the next stone in the other 2 lanes from the current point 
we will shift to the lane that have the stone at the greater distance from the current point;

```
class Solution {
public:
    int minSideJumps(vector<int>& obs) {
        int n=obs.size();
        vector<vector<int>>lane(3,vector<int>(n+1));
        for(int i=0;i<n;i++)
        {
            if(obs[i]==0)
                continue;
            lane[obs[i]-1][i]=1;
        }
        int p=0;
        int ans=0;
        int l=1;
        int s;
        while(p<n)
        {
            while(p<n&&lane[l][p]==0)
            {
                p++;
            }
            if(p==n)
                return ans;
            p--;
            cout<<p<<" ";
            int c1=0;
            int c2=0;
            int a=(l+1)%3;
            int b=(l+2)%3;
            s=p;
            while(s<n&&lane[a][s]==0)
            {
                s++;
                c1++;
            }
            s=p;
            while(s<n&&lane[b][s]==0)
            {
                s++;
                c2++;
            }
            if(c1>c2)
            {
              l=a;
                p=p+c1-1;
                ans++;
            }
            else
            {
                l=b;
                p=p+c2-1;
                ans++;
            }
        }
        return ans;
    }
};
