//Note: Frog jumps to another lane one and only when no forward movement is possible in the same lane.
class Solution {
    boolean [][] matrix;
    int n;
    int [][] dp;
    public int minSideJumps(int[] obstacles) {
        n=obstacles.length;
        matrix=new boolean[3][n];

        dp=new int [3][n];
        for(int [] arr:dp)Arrays.fill(arr,-1);

		//I used a matrix of 3*n dimension for my exploration
        for(int i=0;i<n;i++){
            int val = obstacles[i];
            if(val!=0)matrix[val-1][i]=true; //mark obstacled position
        }
        return helper(1,0,new boolean [3][n]); //lane is counted on 0-indexed basis
    }
    private int helper(int lane, int i, boolean [][]visited){
        if(i==n-2)return 0; //no further jump/movement required, because if u reached (n-2)th position, u can always reach to (n-1)position
        if(visited[lane][i])return 1000000; //if this path is already visited

        //store
        if(dp[lane][i]!=-1)return dp[lane][i];

        //mark visited
        visited[lane][i]=true;

        //move forward when no obstacle in the same lane
        if(!matrix[lane][i+1]){
            int forward = Integer.MAX_VALUE;
            forward = helper(lane,i+1,visited);

            //back-track
            visited[lane][i]=false;
            return dp[lane][i]=forward;
        }
        // else jump
        else{
            //move downward : two possible jumps
            int down=Integer.MAX_VALUE;
            int ans1 = Integer.MAX_VALUE;
            if(lane+1<=2 && !matrix[lane+1][i]){
                ans1 = 1+helper(lane+1,i,visited);
            }
            int ans2 = Integer.MAX_VALUE;
            if(lane+2<=2 && !matrix[lane+2][i]){
                ans2 = 1+helper(lane+2,i,visited);
            }

            down = Math.min(ans1,ans2);

            //up move : two possible jumps
            int up = Integer.MAX_VALUE;
            int ans3 = Integer.MAX_VALUE;
            if(lane-1>=0 && !matrix[lane-1][i]){
                ans3 = 1+helper(lane-1,i,visited);
            }
            int ans4 = Integer.MAX_VALUE;
            if(lane-2>=0 && !matrix[lane-2][i]){
                ans4 = 1+helper(lane-2,i,visited);
            }

            up = Math.min(ans3,ans4);

            //back-track
            visited[lane][i]=false;
            return dp[lane][i] = Math.min(down,up);
        }
    }
}
