class Solution {
public:
    int dp[500005][4];
    int solve(vector<int>& obstacles , int i , int lane){
        if(i == obstacles.size()) return 0;
        if(obstacles[i] == lane) return 1000000000;
        if(dp[i][lane] != -1) return dp[i][lane];
        int res = solve(obstacles , i + 1, lane);
        for(int j = 1 ; j <= 3 ; j++){
            if(lane == j or obstacles[i] == j) continue;
            res = min(res , solve(obstacles,i+1,j) + 1);            
        }
        return dp[i][lane] = res;
    }
    int minSideJumps(vector<int>& obstacles) {
        memset(dp,-1,sizeof(dp));
        return solve(obstacles,0,2);
    }
};
