class Solution {
    private int[][] memo;

    public int minSideJumps(int[] obstacles) {
        int N = obstacles.length;
        memo = new int[N][4];
        for (int i=0;i<N;i++)
        {
            Arrays.fill(memo[i], -1);
        }

        return dfs(obstacles, 2, 0);
    }

	/*
	* currIdx = current position in obstacles array
	* currRow = rowNum i.e 1,2,3
	*/
    private int dfs(int[] obstacles, int currRow, int currIdx)
    {
        if (memo[currIdx][currRow] != -1)
        {
            return memo[currIdx][currRow];
        }

        if (currIdx == obstacles.length - 1)
        {
            return memo[currIdx][currRow] = 0;
        }

        int nextIdx = currIdx + 1;
        int minSwaps = Integer.MAX_VALUE;
        if (obstacles[nextIdx] == currRow)//obstacle in curr row, cant move further
        {
            //side swap
            for (int row = 1; row <= 3; row++)
            {
                //there should not be obstacle in other row where jumping
                if (row != currRow && obstacles[currIdx] != row)
                {
                    //stay at same idx but move sideways
                   minSwaps = Math.min(minSwaps, 1 + dfs(obstacles, row, currIdx));
                }
            }
        }
        else//no obstacle in nextIdx, move ahead in same row
        {
            minSwaps = dfs(obstacles, currRow, currIdx + 1);
        }

        return memo[currIdx][currRow] = minSwaps;
    }
}
```

