    public int minSideJumps(int[] obstacles) {
        //looks like using BT way to double check
        int curLan = 2;
        int ret = 0;
        int len = obstacles.length;

        return getMinJumps(obstacles, curLan, 0, 0);
    }



    //curLan : 1, 2, 3
    //ob[i + 1] : 0, 1, 2, 3
    //curIdx : 0 - len - 1
    //curJump:
    private int getMinJumps(int[] ob, int curLan, int curIdx, int curJump {
        int len = ob.length;
        if (curIdx == len - 1 || isReachingEnd(ob, curLan, curIdx)) {
            return curJump;
        }

        int ret = 0;

        if (ob[curIdx + 1] == curLan) {
            //need to jump
            int nextLan1 = (curLan - 1) % 3 + 2;
            int nextLan2 = (curLan - 1) % 3 + 3;

            nextLan1 = nextLan1 > 3 ? nextLan1 % 3 : nextLan1;
            nextLan2 = nextLan2 > 3 ? nextLan2 % 3 : nextLan2;

            ret =  Math.min(ob[curIdx] == nextLan1 ? Integer.MAX_VALUE : getMinJumps(ob, nextLan1, curIdx, 1 + curJump),
                            ob[curIdx] == nextLan2 ? Integer.MAX_VALUE : getMinJumps(ob, nextLan2, curIdx, 1 + curJump));
        } else {
            ret = getMinJumps(ob, curLan, curIdx + 1, curJump);
        }

        return ret;
    }

    private boolean isReachingEnd(int[] ob, int curLan, int curIdx) {
        int len = ob.length;
        for (int i = curIdx; i < len; i++) {
            if (ob[i] == curLan) {
                return false;
            }
        }

        return true;
    }
