public int memo(int[][] dp,int ci,int cl,int[] obstacles,boolean[][] vst){
        int n=dp[0].length; 
        if(ci==(n-1)){
            return 0;
        }

        if(vst[cl][ci]){return dp[cl][ci];}
        int min=Integer.MAX_VALUE;

        int ocl=cl,oci=ci;
        vst[cl][ci]=true;

        if(obstacles[ci+1]!=cl){  //move
