            cl=(cl+1)%4;
            if(cl==0){cl=1;}
            if(dp[cl][ci]!=-100){  //jump
                int ft=(memo(dp,ci,cl,obstacles,vst)); ft+=1;
               min=Math.min(min, ft);
            }
            ct++;
            } 
