class Solution:
    def minSideJumps(self, obstacles: List[int]) -> int:
        n = len(obstacles) - 1
        dp = [[float('inf')] * 3 for _ in range(n)]
        dp.append([0, 0, 0])
        for i in reversed(range(n)):
            ob_lane = None
            for lane in range(3):
                if obstacles[i] == lane + 1: continue
                if dp[i+1][lane] == float('inf'):
                    ob_lane = lane
                else:
                    dp[i][lane] = dp[i+1][lane]
            if ob_lane is not None: dp[i][ob_lane] = min(dp[i]) + 1
        return dp[0][1]
