```rust
const WIDTH: usize = 3;

impl Solution {
    pub fn min_side_jumps(vec: Vec<i32>) -> i32 {
        let vec = vec.into_iter()
            .rev()
            .map(|e| e as u32)
            .collect::<Vec<_>>();

        let mut dp = [Some(usize::MIN); WIDTH];
        for obstacle in vec {
            let obstacle = obstacle as usize;
            if obstacle > 0 {
                dp[obstacle - 1] = None; // not possible
            }

            for ptr in usize::MIN..WIDTH {
                if ptr + 1 == obstacle {
                    continue;
                }

                for ptr_next in usize::MIN..WIDTH {
                    if let Some(count_next) = dp[ptr_next] {
                        dp[ptr] = Some(
                            dp[ptr]
                                .unwrap_or(usize::MAX)
                                .min(count_next + 1)
                        );
                    }
                }
            }
        }

        dp[1].unwrap() as i32
    }
}
```

