    pair<int,int> check(int lane) {

        pair<int,int> ans;

        if(lane == 1) {

            ans.first = 1;

            ans.second = 2;

        } else if(lane == 2) {

            ans.first = -1;

            ans.second = 1;

        } else if(lane == 3) {

            ans.first = -1;

            ans.second = -2;
        }

        return ans;
    }

    int rec(vector<vector<int>>& dp, vector<int>& o, int lane, int level) {


        if(level == n-1)
            return 0;

        if(dp[lane][level] != -1)
            return dp[lane][level];


        int ans = inf;

        if(!v[lane].count(level+1))
            ans = min(ans, rec(dp, o, lane, level+1));

        else {

            pair<int,int> temp = check(lane);

            // cout << "lane: " << lane << endl;

            // cout << "Side lane: " << lane+temp.first << endl;

            // cout << "Side lane: " << lane+temp.second << endl;

            if(!v[lane+temp.first].count(level))
                ans = min(ans, 1 + rec(dp, o, lane+temp.first, level));

            if(!v[lane+temp.second].count(level))
                ans = min(ans, 1 + rec(dp, o, lane+temp.second, level));
        }

        return dp[lane][level] = ans;
