func minSideJumps(obstacles []int) int {
	// The frog starts in lane 2 at position 0
	curr := 2
	jumps := 0

	var search bool

	for i := 0; i < len(obstacles); i++ {
		if !search {
			if curr == obstacles[i] {
				jumps++
				if obstacles[i-1] != 0 {
					curr = 6 - obstacles[i-1] - curr // we know the line
				} else {
					// look for the best line
					search = true
				}
			}
		} else {
			if obstacles[i] != 0 && obstacles[i] != curr {
				curr = 6 - obstacles[i] - curr
				search = false
			}
		}
	}
	return jumps
}

