class Solution {
public:

    int fun(vector<int>& obstacles, int currPos, int currLane,vector<vector<int>>&dp) {
        // Base case
        int n = obstacles.size() - 1;
        if (currPos == n) {
            return 0;
        }

        if(dp[currLane][currPos]!=-1){
            return dp[currLane][currPos];
        }

        // If there's no obstacle in the next position,
        // no need to change lanes
        if (obstacles[currPos + 1] != currLane) {
            return fun(obstacles, currPos + 1, currLane,dp);
        } else {
            int ans = INT_MAX;
            for (int i = 1; i <= 3; i++) {
                // Check if it's possible to jump to lane i and
                // it's not the current lane
                if (obstacles[currPos] != i && currLane != i) {
                    int jumps = 1 + fun(obstacles, currPos, i,dp);
                    // Jump to lane i
                    ans = min(ans, jumps);
                }
            }
            dp[currLane][currPos]=ans;
            return dp[currLane][currPos];
        }
    }

    int minSideJumps(vector<int>& obstacles) {
        vector<vector<int>>dp(4,vector<int>(obstacles.size(),-1));
        return fun(obstacles, 0, 2,dp); 
        // Start in the middle lane (lane 2)
    }
};

