# Code
```
class Solution {
public:

    int dp(int dist, int lane, vector<vector<int>> &r, vector<vector<int>> &mem){
        if(dist == r[0].size()-1)return 0;
        if(r[lane][dist+1] == 0){
            return dp(dist+1, lane, r, mem);
        }

        // shift to other lanes

        if(mem[lane][dist] != -1)return mem[lane][dist];

        // lane 1
        int lane1 = (r[0][dist] == 0 && lane != 0) ? (dp(dist, 0, r, mem) + 1) : 1e9;
        // lane 2
        int lane2 = (r[1][dist] == 0 && lane != 1)  ? (dp(dist, 1, r, mem) + 1) : 1e9;
        // lane 3
        int lane3 = (r[2][dist] == 0 && lane != 2)  ? (dp(dist, 2, r, mem) + 1): 1e9;

        return mem[lane][dist] = min({lane1, lane2, lane3});
    }

    int minSideJumps(vector<int>& o) {
        int n = o.size();
        vector<vector<int>> r(3, vector<int> (n, 0));
        vector<vector<int>> mem(3, vector<int> (n, -1));
        for(int i=0; i<n; i++){
            if(o[i] == 0)continue;
            else r[o[i]-1][i] = 1;
        }

        return dp(0,1,r, mem);

    }
};
```

