class Solution {
public:
    int dp[(int)5e5 + 5][4];
    int f(vector<int>&o,int pos,int currlane){
        if(pos>=o.size())return 0;
        if(dp[pos][currlane]!=-1)return dp[pos][currlane];

        if(o[pos]==currlane)return dp[pos][currlane]=(int)1e9;

        int o1=f(o,pos+1,currlane);

        for(int i=1;i<=3;i++){ 
            if(o[pos]!=i)
            o1=min(o1 , f(o,pos+1,i) + (i != currlane) );            
        }
        return dp[pos][currlane]=o1;
    }

    int minSideJumps(vector<int>& o) {
        memset(dp,-1,sizeof dp);
        return f(o,0,2);
    }
};
