class Solution {
public:
    bool isPossible(vector<int>& obstacles, int row, int col) {
        int obstaclePosition = obstacles[col];
        if (obstaclePosition == row) {
            return false;
        }
        return true;
    }

    int solve(vector<int>& obstacles, int row, int col, vector<vector<int>>& dp) {
        if (col == obstacles.size()-1) {
            return 0;
        }
        if (dp[row][col] != -1) {
            return dp[row][col];
        }
        int val = 1e9;
        if (isPossible(obstacles, row, col + 1)) {
            int moveAhead = solve(obstacles, row, col + 1, dp);    
            val = min(val, moveAhead);
        } 
        else {
            for (int i = 1; i <= 3; i++) {
                if (isPossible(obstacles, i, col) && i != row) {
                    int adjJump = 1 + solve(obstacles, i, col, dp);
                    val = min(val, adjJump);
                }
            }
        }
        return dp[row][col] =val;
    }

    int minSideJumps(vector<int>& obstacles) {
        int n = obstacles.size();
        vector<vector<int>> dp(4, vector<int>(obstacles.size() + 5, -1));
        return solve(obstacles, 2, 0, dp);
    }
};

