class Solution {
public:
    int solve(int index, int lane, vector<int>& obstacles) {
        if(index == obstacles.size()-1) return 0;

        int ans = INT_MAX;
        if(obstacles[index+1] != lane) {
            return solve(index+1, lane, obstacles);
        }
        else {
            for(int i=1; i<=3; i++) {
                if(lane != i && obstacles[index] != i) {
                    ans = min(ans, solve(index, i, obstacles));
                }
            }
        }
        return ans + 1;
    }

    int solveMem(int index, int lane, vector<int>& obstacles, vector<vector<int>> &dp) {
        if(index == obstacles.size()-1) return 0;

        if(dp[lane][index] != -1) return dp[lane][index];

        if(obstacles[index+1] != lane) {
            dp[lane][index] = solveMem(index+1, lane, obstacles, dp);
            return dp[lane][index];
        }
        int ans = INT_MAX;
        for(int i=1; i<=3; i++) {
            if(lane != i && obstacles[index] != i) {
                ans = min(ans, solveMem(index, i, obstacles, dp));
            }
        }
        dp[lane][index] = ans + 1;
        return dp[lane][index];
    }

    int solveTab(vector<int>& obstacles) {
        int n = obstacles.size()-1;
        vector<vector<int>> dp(4, vector<int>(n+1, 1e9));

        dp[0][n] = 0;
        dp[1][n] = 0;
        dp[2][n] = 0;
        dp[3][n] = 0;

        for(int index=n-1; index>=0; index--) {
            for(int lane=1; lane<=3; lane++) {
                if(obstacles[index+1] != lane) {
                    dp[lane][index] = dp[lane][index+1];
                }
                else {
                    int ans = 1e9;
                    for(int i=1; i<=3; i++) {
                        if(lane != i && obstacles[index] != i) {
                            ans = min(ans, 1+dp[i][index+1]);
                        }
                    }
                    dp[lane][index] = ans ;
                }
            }
        }
        return min(dp[2][0], min(1+dp[1][0], 1+dp[3][0]) );
    }

    int solveTabOpt(vector<int>& obstacles) {
        int n = obstacles.size()-1;
        vector<int> curr(4, 1e9);
        vector<int> prev(4, 1e9);
        prev[0] = 0;
        prev[1] = 0;
        prev[2] = 0;
        prev[3] = 0;

        for(int index=n-1; index>=0; index--) {
            for(int lane=1; lane<=3; lane++) {
                if(obstacles[index+1] != lane) {
                    curr[lane] = prev[lane];
                }
                else {
                    int ans = 1e9;
                    for(int i=1; i<=3; i++) {
                        if(lane != i && obstacles[index] != i) {
                            ans = min(ans, 1+prev[i]);
                        }
                    }
                    curr[lane] = ans ;
                }
            }
            prev = curr;
        }
        return min(prev[2], min(1+prev[1], 1+prev[3]) );
    }

    int minSideJumps(vector<int>& obstacles) {
        int n = obstacles.size();
        vector<vector<int>> dp(4, vector<int>(n, -1));
        return solveTabOpt(obstacles);
        return solveTab(obstacles);
        return solveMem(0, 2, obstacles, dp);
        return solve(0, 2, obstacles);
    }
};
```

