    vector<vector<int>> dp;
    int minSideJumpsHelper(int i,vector<int>& obstacles,int lane){
        if(i>=obstacles.size()-1){
            return 0;
        }
        if(dp[i][lane]!=-1){
            return dp[i][lane];
        }
        int stoneLane=obstacles[i+1];
        int currStone=obstacles[i];
        int ansSidelane1=INT_MAX-1,ansSidelane2=INT_MAX-1,ansSamelane=INT_MAX-1;
        if(stoneLane==lane){
            if(lane==1){
                // jump to lane 2 and 3
                if(lane+1!=currStone){
                    ansSidelane1=minSideJumpsHelper(i+1,obstacles,lane+1);
                }
                if(lane+2!=currStone){
                    ansSidelane2=minSideJumpsHelper(i+1,obstacles,lane+2);
                }
            }
            else if(lane==2){
                // jump to lane 1 and 3
                if(lane-1!=currStone){
                    ansSidelane1=minSideJumpsHelper(i+1,obstacles,lane-1);
                }
                if(lane+1!=currStone){
                    ansSidelane2=minSideJumpsHelper(i+1,obstacles,lane+1);
                }
            }
            else{
                // jump tolane 1 and 2
                if(lane-1!=currStone){
                    ansSidelane1=minSideJumpsHelper(i+1,obstacles,lane-1);
                }
                if(lane-2!=currStone){
                    ansSidelane2=minSideJumpsHelper(i+1,obstacles,lane-2);
                }
            }
        }
        else{
            ansSamelane=minSideJumpsHelper(i+1,obstacles,lane);
        }
        int ansJumplane=INT_MAX-1;
        if(ansSidelane1==INT_MAX-1 && ansSidelane2==INT_MAX-1){
            ansJumplane=INT_MAX-1;
        }
        else{
            ansJumplane=1+min(ansSidelane1,ansSidelane2);
        }
