class Solution {
public:
    /*
    Thoughts
    1)We need to find the min no of sideways jumps needed for point just before n point
    2)In base case if you are at the destination already then return 0 as no side jumps needed
    3)How to perform a small calculation?
    ->we need to perform some calculation over obstacles
    ->We need to keep track of current lane
    ->side jumps are possible only when the lane we are switching to are different from same lane and the lane at that point having stone
    ->We have some ways to jump over which we can call recurance and min the answer
    ->there can be no case in which we can not take any lane
    ->after every side jump perform a +1
    */
    int solve(vector<int>& obstacles,int i,int walkinglane,vector<vector<int>>& strg){
        //If we are at i==n then please tell that we need now 0 side jumps!
        if(i==obstacles.size()-1) return 0; 
        if(strg[i][walkinglane]!=-1) return strg[i][walkinglane];
        //If we remain in same lane we need 0 side jumps additionally
        int remainInSameLane=(obstacles[i]!=walkinglane)?solve(obstacles,i+1,walkinglane,strg) : INT_MAX;
        //We can go to another lane if that lane is not having stones or is our current lane
        int goToAnotherLane=INT_MAX;
        for(int lane=1;lane<=3;lane++){
            if(lane!=obstacles[i-1] && lane!=walkinglane && lane!=obstacles[i]){
                goToAnotherLane=min(goToAnotherLane,1+solve(obstacles,i+1,lane,strg));
            }
        }
        int sideJumps=min(remainInSameLane,goToAnotherLane);
        return strg[i][walkinglane] = sideJumps;
    }
    int minSideJumps(vector<int>& obstacles) {
        vector<vector<int>> strg(obstacles.size()+1,vector<int>(4,-1));
        //Keep track of point and the lane
        return solve(obstacles,1,2,strg);
    }
};
```

