class Solution {
    int solve(vector<int>& obstacles,int i,int j,int n,vector<vector<int>>& dp){
        if(i==n-1) return 0;
        if(dp[i][j]!=-1) return dp[i][j];
        int ans=0;
        if(obstacles[i+1]==0 || obstacles[i+1]!=j){
            ans=0+solve(obstacles,i+1,j,n,dp);
        }
        else if(obstacles[i+1]==j){
            if(j==2){
              if(obstacles[i]==1) ans+=1+solve(obstacles,i,j+1,n,dp);
              else if(obstacles[i]==3) ans+=1+solve(obstacles,i,j-1,n,dp);
              else ans+=min(1+solve(obstacles,i,j+1,n,dp),1+solve(obstacles,i,j-1,n,dp));
            }
            else if(j==1){
                if(obstacles[i]==2) ans+=1+solve(obstacles,i,j+2,n,dp);
                else if(obstacles[i]==3) ans+=1+solve(obstacles,i,j+1,n,dp);
                else ans+=min(1+solve(obstacles,i,j+2,n,dp),1+solve(obstacles,i,j+1,n,dp));
            }
            else if(j==3){
                 if(obstacles[i]==2) ans+=1+solve(obstacles,i,j-2,n,dp);
                else if(obstacles[i]==1) ans+=1+solve(obstacles,i,j-1,n,dp);
                else ans+=min(1+solve(obstacles,i,j-2,n,dp),1+solve(obstacles,i,j-1,n,dp));
            }
        }
        return dp[i][j]=ans;
    }
public:
    int minSideJumps(vector<int>& obstacles) {
        int n=obstacles.size();
        vector<vector<int>> dp(n,vector<int> (4,-1));
        int ans=solve(obstacles,0,2,n,dp);
        return ans;
    }
};
