                    return dp[start][lane]=1+solve(path,start,end,lane-1,dp);
                }
                else{
                    return dp[start][lane]=1+solve(path,start,end,lane+1,dp);
                }}
            else{
                if(path[start][lane-1]==0 && path[start][lane-2]==0){
                    return dp[start][lane]=Math.min(1+solve(path,start,end,lane-1,dp),1+solve(path,start,end,lane-2,dp));}
