class Solution {
public:
    int solve(vector<int>&v,int i,int l,int n,vector<vector<int>>&dp){
        if(i>=n-1)return 0;
        if(v[i]==l)return 1e8;
        if(dp[i][l]!=-1)return dp[i][l];

        // when next point is clear
        if(v[i+1]!=l)
            return dp[i][l] = solve(v,i+1,l,n,dp);

        // when next point has an obstacle
        if(l==2)
            return dp[i][l] = min(1+solve(v,i,3,n,dp),1+solve(v,i,1,n,dp));
        else if(l==1) return dp[i][l] = min(1+solve(v,i,2,n,dp),1+solve(v,i,3,n,dp));

        else return dp[i][l] = min(1+solve(v,i,2,n,dp),1+solve(v,i,1,n,dp));

    }
    int minSideJumps(vector<int>& v) {
        int n=v.size();
        int i=0,l=2;
        vector<vector<int>>dp(n,vector<int>(4,-1));
        return solve(v,i,l,n,dp);

    }
};
