```
class MKAverage {
    TreeSet<Long> small, medium, large;
    Queue<Long> queue;
    long sum = 0;
    int m, k, total;
    long ind = 0;
    public MKAverage(int m, int k) {
        queue = new ArrayDeque();
        small = new TreeSet();
        large = new TreeSet();
        medium = new TreeSet();
        this.m = m;
        this.k = k;
        total = m - 2*k;
    }

    // each operation at O(logn)
    public void addElement(int num) {
        ind ++;
        long n = num*1000000L+ind;
        if(queue.size() < m){
            queue.add(n);

            //one time operation - O(nlogn)
            if(queue.size() == m){
                List<Long> al = new ArrayList(queue);
                Collections.sort(al);
                for(int i=0;i<k;i++){
                    small.add(al.get(i));
                }
                for(int i=k;i<al.size()-k;i++){
                    sum += al.get(i);
                    medium.add(al.get(i));
                }
                for(int i=al.size()-k;i<al.size();i++){
                    large.add(al.get(i));
                }
            }
        }else{
            long val = queue.poll();
            queue.add(n);

            long mFirst = medium.first();
            long mLast = medium.last();
            if(medium.contains(val)){
                medium.remove(val);
                sum -= val;
            }else if(small.contains(val)){
                small.remove(val);
            }else{
                large.remove(val);
            }

            if(n < mFirst){
                small.add(n);
            }else if(n > mFirst && n < mLast){
                medium.add(n);
                sum += n;
            }else{
                large.add(n);
            }

            if(large.size() == k+1){
                long largeFirst = large.first();
                large.remove(largeFirst);
                medium.add(largeFirst);
                sum += largeFirst;
            }
            if(small.size() == k+1){
                long smallLast = small.last();
                small.remove(smallLast);
                medium.add(smallLast);
                sum += smallLast;
            }
            if(small.size() == k-1){
                long mediumFirst = medium.first();
                small.add(mediumFirst);
                medium.remove(mediumFirst);
                sum -= mediumFirst;
            }
            if(large.size() == k-1){
                long mediumLast = medium.last();
                medium.remove(mediumLast);
                large.add(mediumLast);
                sum -= mediumLast;
            }
        }
    }

    public int calculateMKAverage() {
        if(queue.size() < m){
            return -1;
        }
        return (int)(sum/(1000000L*total)); 
    }
}
