class MKAverage {
    Deque<Integer> queue;
    Bit countBit;
    Bit valueBit;
    int m;
    int k;
    public MKAverage(int m, int k) {
        queue = new ArrayDeque();
        countBit = new Bit(100001);
        valueBit = new Bit(100001);
        this.m = m;
        this.k = k;
    }

    public void addElement(int num) {
        queue.addLast(num);
        countBit.add(num, 1);
        valueBit.add(num, num);
        if(queue.size()>m)
        {
            var polledNum = queue.pollFirst();
            countBit.add(polledNum, -1);
            valueBit.add(polledNum, -polledNum);
        }
    }

    public int calculateMKAverage() {
        if(queue.size() < m)
            return -1;

		// Get first k elements index
        var left = binarySearch(k);

		// Get last k elements index
        var right = binarySearch(m-k);

        // Ex: [5 5 5] 
        // left and right are equal, so difference is 0. What this means is the answer is at either left or right.
        // Using the countBit we can calculate the correct sum at index left.
        // Thats why we also have to do step 1 and step 2. 
        long result = valueBit.sum(right) - valueBit.sum(left); 

        result += (countBit.sum(left) - k)*left; // step 1
        result -= (countBit.sum(right) - m + k)*right; // step 2

        return (int)(result/(m-2*k));
    }

    public int binarySearch(int val)
    {
        int l = 0;
        int r = 100001;
        while(l<r)
        {
            int mid = l + (r-l)/2;
            if(countBit.sum(mid) < val)
                l = mid + 1;
            else 
                r = mid;
        }

        return l;
    }


    class Bit
    {
	    // Use long because the sum can exceed Integer.MAX_VALUE
        long[] bit;
        public Bit(int size)
        {
			// 1-indexed
            bit = new long[size+1];
        }

        public void add(int num, int value)
        {         
            num += 1;
            while(num < bit.length)
            {
                bit[num] += value;              
                num += num & (-num);
            }
        }

        public long sum(int num)
        {
            long s = 0;
            num += 1;
            while(num>0)
            {
                s += bit[num];
                num &= num-1;
            }

            return s;
        }
    }
}
