class MKAverage {
    int m;
    int k;
    List<Integer> lastM; // Record last m numbers, ordered by when they were added. This is for removing number.
    TreeMap<Integer, Integer> middle; // Record the numbers in the middle. Remains empty until we have more than 2*k numbers.
    PriorityQueue<Integer> smallerK;
    long smallerSum;
    PriorityQueue<Integer> largerK;
    long largerSum;
    long sum;
    public MKAverage(int m, int k) {
        this.m = m;
        this.k = k;
        lastM = new ArrayList<>();
        middle = new TreeMap<>();
        smallerK = new PriorityQueue<>((a, b) -> b - a);
        smallerSum = 0;
        largerK = new PriorityQueue<>();
        largerSum = 0;
        sum = 0;
    }

    public void addElement(int num) {
        /**
		 * First, check if need to remove number.
		 * If yes, remove it from the head of lastM and update total sum.
		 * Also check if need to remove it from middle M, smaller K or larger K, and update smaller sum and larger sum.
		 */
        if (lastM.size() == m) {
            int toRemove = lastM.remove(0);
            sum -= toRemove;
            if (middle.containsKey(toRemove)) {
                middle.put(toRemove, middle.get(toRemove) - 1);
                if (middle.get(toRemove) == 0) {
                    middle.remove(toRemove);
                }
            }

            if (toRemove <= smallerK.peek()) {
                smallerK.remove(toRemove);
                smallerSum -= toRemove;
                int toOffer = middle.firstKey();
                middle.put(toOffer, middle.get(toOffer) - 1);
                if (middle.get(toOffer) == 0) {
                    middle.remove(toOffer);
                }
                smallerK.offer(toOffer);
                smallerSum += toOffer;
            } else if (toRemove >= largerK.peek()) {
                largerK.remove(toRemove);
                largerSum -= toRemove;
                int toOffer = middle.lastKey();
                middle.put(toOffer, middle.get(toOffer) - 1);
                if (middle.get(toOffer) == 0) {
                    middle.remove(toOffer);
                }
                largerK.offer(toOffer);
                largerSum += toOffer;
            }
        }

		/**
		 * Add it to the tail of lastM, and update total sum.
		 * Set the number to add to middle m to just be the current num for now, this might change if num is put into either smaller/larger half.
		 */
        lastM.add(num);
        int addToMiddle = num;
        sum += num;
        if (smallerK.size() < k) {
		    /**
			 * Don't have k numbers yet, simply add the number to both halves and update smaller and larger sum.
			 */
            smallerK.offer(num);
            smallerSum += num;
            largerK.offer(num);
            largerSum += num;
        } else {
		    /**
			 * Have more than k numbers, check whether to put the number to smaller k or larger k.
			 * 
			 * The reason to do both check is that when we don't have 2*k numbers, the two halves will overlap,
			 * the number may still need to be in both halves.
			 * 
			 * Update the eventual number to be added to middle m.
			 */
            if (num <= smallerK.peek()) {
                addToMiddle = smallerK.poll();
                smallerSum -= addToMiddle;
                smallerK.offer(num);
                smallerSum += num;
            }

            if (num >= largerK.peek()) {
                addToMiddle = largerK.poll();
                largerSum -= addToMiddle;
                largerK.offer(num);
                largerSum += num;
            }
        }

		/**
		 * Finally, only add to middle m if we have more than 2*k numbers.
		 */
        if (lastM.size() > 2 * k) {
            middle.put(addToMiddle, middle.getOrDefault(addToMiddle, 0) + 1);
        }
    }

    public int calculateMKAverage() {
        if (lastM.size() != m) {
            return -1;
        }

        return (int) ((sum - smallerSum - largerSum) / (m - 2 * k));
    }
}

