class MKAverage {
    private MultiTreeSet bottom = new MultiTreeSet();
    private MultiTreeSet middle = new MultiTreeSet();
    private MultiTreeSet top = new MultiTreeSet();    
    private int m, k;
    private LinkedList<Integer> elements = new LinkedList<>();
    private long sum = 0;
    public MKAverage(int m, int k) {
        this.m = m;
        this.k = k;
    }

    public void addElement(int num) {
        elements.add(num);
        bottom.add(num); // blindly add to bottom
		// remove the element from the corresponding stack
        if (elements.size() > m) {
            int removed = elements.removeFirst();
            if (bottom.contains(removed)) {
                bottom.remove(removed);
            } else if (middle.contains(removed)) {
                middle.remove(removed);
                sum -= removed;
            } else if (top.contains(removed)) {
                top.remove(removed);
            }
        }
        if (elements.size() == m) {
		    // once we reach m, bottom can only be >= k (because each call, we add 1 element to bottom, and remove at most 1 element from it)
            while (bottom.total > k) {
                int element = bottom.pollLast();
                middle.add(element);
                sum += element;
            }
			// fix size of middle stack
            while (middle.total > m - 2*k) {
                int element = middle.pollLast();
                top.add(element);
                sum -= element;
            }
            // fix ordering of bottom-middle stack
            while (bottom.getLast() > middle.getFirst()) {
                int e1 = middle.pollFirst();
                int e2 = bottom.pollLast();
                middle.add(e2);
                bottom.add(e1);
                sum = sum - e1 + e2;
            }
            // fix ordering of middle-top stack
            while (middle.getLast() > top.getFirst()) {
                int e1 = top.pollFirst();
                int e2 = middle.pollLast();
                middle.add(e1);
                top.add(e2);
                sum = sum + e1 - e2;
            }
        }
    }

    public int calculateMKAverage() {
        return elements.size() == m ? (int)(sum/(m - 2*k)) : -1;
    }

    static class MultiTreeSet {
        private TreeMap<Integer, Integer> map = new TreeMap<Integer, Integer>();
        private int total = 0;

        public void add(int val) {
            int count = map.getOrDefault(val, 0) + 1;
            map.put(val, count);
            total++;
        }

        public void remove(int val) {
            int count = map.get(val);
            if (count == 1) map.remove(val);
            else map.put(val, count-1);
            total--;
        }

        public boolean contains(int val) {
            return map.containsKey(val);
        }

        public int getFirst() {
            return map.firstKey();
        }

        public int getLast() {
            return map.lastKey();
        }

        public int pollFirst() {
            int first = map.firstKey();
            remove(first);
            return first;
        }

        public int pollLast() {
            int last = map.lastKey();
            remove(last);
            return last;
        }
    }
}

