```
class MKAverage {
public:
    #define LL long long
    struct node {
        int frq;
        LL sum;

        node() { frq = 0; sum = 0; }
        node(int _frq, LL _sum): frq(_frq), sum(_sum) {}
    };

    node Tree[100005 * 4];
    const int limit = 100000;


    node merge_segments(const node &lf, const node &rt) {
        return node(lf.frq + rt.frq, lf.sum + rt.sum);
    }

    void update(int node, int b, int e, int pos, int add) {
        if (b == e) {
            Tree[node].frq += add;
            Tree[node].sum += (add * pos);
            return ;
        }
        int mid = (b + e) >> 1;
        int lf = node << 1;
        int rt = lf | 1;

        if (pos <= mid) update(lf, b, mid, pos, add);
        else update(rt, mid + 1, e, pos, add);

        Tree[node] = merge_segments(Tree[lf], Tree[rt]);
    }


    LL get_left_kth_sum(int node, int b, int e, int kth) {
        if (kth <= 0) return 0;
        if (kth >= Tree[node].frq) return Tree[node].sum;
        if (b == e) {
            return kth * 1LL * b;
        }

        int mid = (b + e) >> 1;
        int lf = node << 1;
        int rt = lf | 1;

        return get_left_kth_sum(lf, b, mid, kth) + get_left_kth_sum(rt, mid + 1, e, kth - Tree[lf].frq);
    }

    LL get_right_kth_sum(int node, int b, int e, int kth) {
        if (kth <= 0) return 0;
        if (kth >= Tree[node].frq) return Tree[node].sum;
        if (b == e) {
            return kth * 1LL * b;
        }

        int mid = (b + e) >> 1;
        int lf = node << 1;
        int rt = lf | 1;

        return get_right_kth_sum(lf, b, mid, kth - Tree[rt].frq) + get_right_kth_sum(rt, mid + 1, e, kth);
    }

    int M, K;
    queue <int> window;

    MKAverage(int m, int k) {
        M = m;
        K = k;
    }

    void addElement(int num) {
        window.push(num);
        update(1, 0, limit, num, +1);

        if (window.size() > M) {
            update(1, 0, limit, window.front(), -1);
            window.pop();
        }

    }

    int calculateMKAverage() {
        if (window.size() < M) return -1;
        LL total = Tree[1].sum;
        LL left_k_sum = get_left_kth_sum(1, 0, limit, K);
        LL right_k_sum = get_right_kth_sum(1, 0, limit, K);


        return (total - left_k_sum - right_k_sum) / (M - 2 * K);
    }
};

