solutions.content:
The idea is to have addElement and calculateMKAverage both in O(log m), although it still is O(m) in the worst case: a left only(or right only) childs tree.

```
class BST {
    struct node {
        int data;
        node* left;
        node* right;
        int lCount;
        int rCount;
        int lsum;
        int rsum;
        int multiplicity;
    };

    node* insert(int x, node* t) {
        if(t == NULL)
        {
            t = new node;
            t->data = x;
            t->lCount = 0;
            t->rCount = 0;
            t->lsum = 0;
            t->rsum = 0;
            t->multiplicity = 1;
            t->left = t->right = NULL;
        }
        else if(x < t->data)
        {
            t->lCount++;
            t->lsum += x;
            t->left = insert(x, t->left);
        }
        else if(x > t->data)
        {
            t->rCount++;
            t->rsum += x;
            t->right = insert(x, t->right);
        } else {
            t->multiplicity++;
        }
        return t;
    }

    node* findMin(node* t) {
        if(t == NULL)
            return NULL;
        else if(t->left == NULL)
            return t;
        else
            return findMin(t->left);
    }

    node* fullRemove(int x, node* t, int qt, int sr) {
        node* temp;

        // Element not found
        if(t == NULL)
            return NULL;

        // Searching for element
        else if(x < t->data) {
            t->lCount-=qt;
            t->lsum -= sr;
            t->left = fullRemove(x, t->left, qt,sr);
        }
        else if(x > t->data) {
            t->rCount-=qt;
            t->rsum -= sr;
            t->right = fullRemove(x, t->right, qt,sr);
        }

        // Element found
        // With one or zero child
        else
        {
            temp = t;
            if(t->left == NULL)
                t = t->right;
            else if(t->right == NULL)
                t = t->left;
            delete temp;
        }

        return t;
    }

    node* remove(int x, node* t) {
        node* temp;

        // Element not found
        if(t == NULL)
            return NULL;

        // Searching for element
        else if(x < t->data) {
            t->lCount--;
            t->lsum -= x;
            t->left = remove(x, t->left);
        }
        else if(x > t->data) {
            t->rCount--;
            t->rsum -= x;
            t->right = remove(x, t->right);
        }

        // Element found
        // repeated
        else if(t->multiplicity > 1) {
            t->multiplicity--;
        }
        // With 2 children
        else if(t->left && t->right)
        {
            temp = findMin(t->right);
            t->data = temp->data;
            t->rCount-=temp->multiplicity;
            t->rsum -= t->data*temp->multiplicity;
            t->right = fullRemove(t->data, t->right,temp->multiplicity,t->data*temp->multiplicity);
        }
        // With one or zero child
        else
        {
            temp = t;
            if(t->left == NULL)
                t = t->right;
            else if(t->right == NULL)
                t = t->left;
            delete temp;
        }
        if(t == NULL)
            return t;

        return t;
    }

    node* root;
    void kSmallestSumRec(node *root, int k , int &temp_sum) {
        if (root == NULL)
            return ;

        // if we fount k smallest element then break the function
        if ((root->lCount + root->multiplicity) == k) {
            temp_sum += root->data*root->multiplicity + root->lsum ;
            return ;
        }

        else if (k > root->lCount)
        {
            // store sum of all element smaller than current root ;
            temp_sum += root->lsum;
            k -= root->lCount;

            if(root->multiplicity >= k) {
                temp_sum += root->data*k;
                return;
            } else {
                temp_sum += root->data*root->multiplicity;
                k -= root->multiplicity;
            }

            kSmallestSumRec(root->right , k , temp_sum);
        }
        else // call left sub-tree
            kSmallestSumRec(root->left , k , temp_sum );
    }

    void kGreatestSumRec(node *root, int k , int &temp_sum) {
        if (root == NULL)
            return ;

        // if we fount k smallest element then break the function
        if ((root->rCount + root->multiplicity) == k) {
            temp_sum += root->data*root->multiplicity + root->rsum ;
            return ;
        }

        else if (k > root->rCount)
        {
            temp_sum += root->rsum;
            k -= root->rCount;

            if(root->multiplicity >= k) {
                temp_sum += root->data*k;
                return;
            } else {
                temp_sum += root->data*root->multiplicity;
                k -= root->multiplicity;
            }

            kGreatestSumRec(root->left, k , temp_sum);
        }
        else // call left sub-tree
            kGreatestSumRec(root->right, k , temp_sum);
    }

    public:
    BST() {
        root = NULL;
    }

    void insert(int x) {
        root = insert(x, root);
    }

    void remove(int x) {
        root = remove(x, root);
    }

    int kElementSum(int k) {
        int sum = 0;
        kSmallestSumRec(root, k, sum);
        kGreatestSumRec(root, k, sum);
        return sum;
    }
};

class MKAverage {
    long long sum;
    queue<int> elements;
    int m;
    int k;
    BST* tree;
public:
    MKAverage(int m, int k):m(m), k(k), sum(0) {
        tree = new BST();
    }

    void addElement(int num) {
        if(elements.size() == m) {
            sum = sum - elements.front();
            tree->remove(elements.front());
            elements.pop();
        }

        sum = sum + num;
        tree->insert(num);
        elements.push(num);
    }

    int calculateMKAverage() {
        if(elements.size() < m) return -1;

        return (sum - tree->kElementSum(k))/(m - 2*k);
    }
};
