class MKAverage {
public:
    vector<int> items;
    map<int,int> lastM;
    int m,k,s,e;
    long total;

    MKAverage(int m, int k) {
        this->m = m;
        this->k = k;
        s = 0;
        e = 0;
        total = 0;
    }

	// O(logM)
    void addElement(int num) {
        items.push_back(num);
        lastM[num]++;
        total += num;
        e++;

        if(e-s>m) 
        {
            if(--lastM[items[s]] == 0) lastM.erase(items[s]);
            total -= items[s++];
        }
    }

	// O(K)
    int calculateMKAverage() {
        if(e - s < m) return -1;
        int n = k, minVal = 0, maxVal = 0;
        auto itr = lastM.begin();
        while(n>0) 
        {
            minVal += itr->first * min(itr->second, n);
            n -= min(itr->second, n);
            if(n > 0)
            {
                itr++;
            }
        }

        auto _itr = lastM.rbegin();
        n = k;
        while(n>0) 
        {
            maxVal += _itr->first * min(_itr->second, n);
            n -= min(_itr->second, n);
            if(n > 0)
            {
                _itr++;
            }
        }

        return (total - minVal - maxVal) / (m-2*k);
    }
};
