class MultiMap {
    public TreeMap<Integer, Integer> map;
    private int cnt = 0;
    private double sum = 0.0;

    public MultiMap() {
        this.map = new TreeMap<>();
    }

    public void add(Integer key) {
        this.cnt ++;
        this.sum += key;
        this.map.put(key, this.map.getOrDefault(key, 0) + 1);
    }

    public boolean containsKey(Integer key) {
        return this.map.getOrDefault(key, 0) > 0;
    }

    public void remove(Integer key) {
        if (!this.map.containsKey(key)) {
            return;
        }

        this.cnt --;
        this.sum -= key;
        this.map.put(key, this.map.get(key)-1);

        if (this.map.get(key) == 0) {
            this.map.remove(key);
        }
    }

    public Integer getMin() {
        return this.map.firstKey();
    }

    public Integer getMax() {
        return this.map.lastKey();
    }

    public int avg() {
        return (int)(this.sum/this.cnt);
    }

    public int size() {
        return this.cnt;
    }
}

class TripleMap {
    public MultiMap small;
    public MultiMap mid;
    public MultiMap big;
    private int m;
    private int k;

    public TripleMap(int m, int k) {
        this.small = new MultiMap();
        this.mid = new MultiMap();
        this.big = new MultiMap();
        this.m = m;
        this.k = k;
    }

    public void add(int num) {
        if (mid.size() == 0) {
            mid.add(num);
            return;
        }

        if (num < mid.getMin()) {
            small.add(num);
        } else if (num > mid.getMax()) {
            big.add(num);
        } else {
            mid.add(num);
        }

        this.balance();
    }

    private void balance() {
        int size = small.size() + mid.size() + big.size();

        if (size < m) {
            return;
        }

        while (small.size() > k) {
            int victim = small.getMax();
            small.remove(victim);
            mid.add(victim);
        }

        while (big.size() > k) {
            int victim = big.getMin();
            big.remove(victim);
            mid.add(victim);
        }

        while (small.size() < k) {
            int victim = mid.getMin();
            mid.remove(victim);
            small.add(victim);
        }

        while (big.size() < k) {
            int victim = mid.getMax();
            mid.remove(victim);
            big.add(victim);
        }
    }

    public void remove(int num) {
        if (small.containsKey(num)) {
            small.remove(num);
        } else if (mid.containsKey(num)) {
            mid.remove(num);
