import bisect
class MKAverage:

    def __init__(self, m: int, k: int):
        self.m = m
        self.k = k
        self.list = [] ##Used as a sorted list. Elements are sorted in non decreasing order
        self.added = [] ##Used as a queue
        self.sum = None

    def addElement(self, num: int) -> None:
        ln = len(self.list)
        if ln < self.m:
            self.added.append(num)
            index = bisect.bisect_right(self.list, num)
            self.list.insert(index, num)
            if len(self.list) == self.m:
                self.sum = sum(self.list[self.k:self.m-self.k])
        else:
			## Pop the element to get the last m-1 elements added
            x = self.added.pop(0)
            self.added.append(num)
            pop_index = bisect.bisect_left(self.list, x)
            if pop_index<self.k:
                self.sum -= self.list[self.k]
            elif pop_index >= self.m-self.k:
                self.sum -= self.list[self.m-self.k-1]
            else:
                self.sum -= x
            self.list.pop(pop_index)

			##Get the index of the new element to be added in the sorted list
            add_index = bisect.bisect_right(self.list, num)
            self.list.insert(add_index, num)
            if add_index<self.k:
                self.sum += self.list[self.k]
            elif add_index >= self.m-self.k:
                self.sum += self.list[self.m-self.k-1]
            else:
                self.sum += num

    def calculateMKAverage(self) -> int:
        if len(self.list)<self.m:
            return -1
        else:
            return self.sum//(self.m - 2*self.k)
