			// now we need to insert elements into middle set depending upon the following conditions
            else{
                auto it=low.end();
                --it;
                auto it2=high.begin();
				// if current number is one of the smallest then insert into low
				// insert the max of low into middle set
                if(num<*it){
                    ll val=*it;
                    low.erase(it);
