                        ll val=*it;
                        midsum-=val;
                        mid.erase(it);
                        low.insert(val);
                    }
					// deletion from high reduces its size so we insert the max from middle into high set
                    else{
                        high.erase(high.find(f));
                        auto it=mid.end();
                        --it;
                        ll val=*it;
                        mid.erase(it);
                        midsum-=val;
                        high.insert(val);
                    }
                } 
            }
        }
    }    

    int calculateMKAverage() {
        ll ls=low.size(),ms=mid.size(),hs=high.size();
        tot=ls+ms+hs;
		// if tot size < m
        if(tot<m)
            return -1;
		// calculating the average
