class MKAverage {
public:
    int m,k;
    int temp;
    multiset<int,greater<int>> s;
    vector<int> v;

    MKAverage(int m, int k) {
        this->m = m;
        this->k = k;
        v.clear();
        temp = 0;
    }

    void addElement(int num) {
        v.push_back(num);
        if(s.size()<m){
            s.insert(num);
        }else{
            s.erase(s.lower_bound(v[temp]));
            temp++;
            s.insert(num);
        }
    }

    int calculateMKAverage() {
        if(s.size()<m) return -1;
        vector<int> arr;
        for(auto it:s){
            arr.push_back(it);
        }
        long long ress=0;
        long long count = 0;
        int i=k;
        for(int j=k;j<arr.size();j++){
            i++;
            if(i>k && i<=(m-k)){
                ress+=arr[j];
                count++;
            }
            if(i>(m-k)) break;
        }
        int res = ress/count ;
        return res;
    }
};

