class MKAverage {
    int m , k;
    LinkedList<Integer> q = new LinkedList<>();
    MinMaxQ head = new MinMaxQ();
    MinMaxQ body = new MinMaxQ();
    MinMaxQ tail = new MinMaxQ();

    public MKAverage(int m, int k) {
        this.m=m;
        this.k=k;
    }

    public void addElement(int num) {
        if (q.size()==m) {
            int pre = q.poll();
            if (tail.contains(pre)) {
                tail.remove(pre);
            } else if (body.contains(pre)) {
                body.remove(pre);
                body.add(tail.pollMin());
            } else if (head.contains(pre)){
                head.remove(pre);
                body.add(tail.pollMin());
                head.add(body.pollMin());
            }
        }

        head.add(num);
        if (head.count>k) {
            body.add(head.pollMax());
            if (body.count>(m-2*k)) 
                tail.add(body.pollMax());
        }
        q.offer(num);
    }

    public int calculateMKAverage() {
        if (q.size()<m) return -1; 
        return (int)Math.floor(body.sum/(m-2*k));
    }

    class MinMaxQ {
        int count = 0;
        long sum = 0;
        TreeMap<Integer, Integer> map = new TreeMap<>();
        Integer min() { return map.firstKey();}
        Integer max() { return map.lastKey();}
        int count() { return count;}
        long sum() { return sum;}
        boolean contains(int num) { return map.containsKey(num);}
        boolean isEmpty(){ return map.isEmpty();}

        int add(int num) {
            map.merge(num, 1, (a,b)->a+b);
            sum+=num;
            return ++count;
        }

        Integer pollMin() {
            Integer min = this.min();
            if (min!=null) remove(min);
            return min;
        }

        Integer pollMax() {
            Integer max = this.max();
            if (max!=null) remove(max);
            return max;
        }

        boolean remove(int num) {
            if (!map.containsKey(num)) return false;
            map.merge(num, -1, (a,b)->a+b);
            if (map.get(num)==0) 
                map.remove(num);
            count--;
            sum-=num;
            return true;
        }
    }
}
