// Time complexity : O(logN)
// Space complexity : O(N)
// N is the possible max value in stream, which is 100000 based on the description

class MKAverage {

    private int LIMIT = 100000;

    int m, k;
    long sum;
    Queue<Integer> q;
    BIT bit1, bit2;

    public MKAverage(int m, int k) {
        this.m = m;
        this.k = k;
        sum = 0;
        q = new LinkedList<>();
        bit1 = new BIT(LIMIT + 1);
        bit2 = new BIT(LIMIT + 1);
    }

    public void addElement(int num) {
        q.offer(num);
        bit1.update(num, 1);
        bit2.update(num, num);
        sum += num;

        if (q.size() > m) {
            int r = q.poll();
            bit1.update(r, -1);
            bit2.update(r, -r);
            sum -= r;
        }
    }

    public int calculateMKAverage() {
        if (q.size() < m) return -1;

        long lSum = findKSmallest(),
             rSum = findKLargest();

        return (int)((sum - lSum -rSum) / (m - 2 * k));

    }

    private long rangQuery(BIT bit, int hig, int low) {
        return bit.query(hig) - bit.query(low - 1);
    }

    private long findKSmallest() {
        int l = 1, r = LIMIT, number = -1;
        while (l + 1 < r) {
            int m = l + (r - l) / 2;
            long count = rangQuery(bit1, m, 1);
            if (count >= k) r = m;
            else l = m;
        }

        if (rangQuery(bit1, l, 1) >= k) number = l;
        else number = r;

        long cnt = rangQuery(bit1, number, 1),
             sum = rangQuery(bit2, number, 1);

        return removeMoreThanK(sum, cnt, number);
    }

    private long findKLargest() {
        int l = 1, r = LIMIT, number = -1;
        while (l + 1 < r) {
            int m = l + (r - l) / 2;
            long count = rangQuery(bit1, LIMIT, m);
            if (count >= k) l = m;
            else r = m;
        }

        if (rangQuery(bit1, LIMIT, r) >= k) number = r;
        else number = l;

        long cnt = rangQuery(bit1, LIMIT, number),
             sum = rangQuery(bit2, LIMIT, number);

        return removeMoreThanK(sum, cnt, number);
    }

    private long removeMoreThanK(long sum, long cnt, int number) {
        if (cnt > k) {
            return sum - (cnt - k) * number;
        }
        return sum;
    }

    class BIT {
        long[] array;

        BIT(int size) {
            array = new long[size];
        }

        public void update(int ind, int v) {
            while (ind < array.length) {
                array[ind] += v;
                ind += ind & -ind;
