class LazySumHeap:

    def __init__(self, key):
        self.heap = []
        self.key = key
        self.c = Counter()
        self.sz = 0
        self.sum = 0

    def __len__(self):
        return self.sz

    def clean(self):
        while self.heap and not self.c[self.heap[0][2]]:
            heappop(self.heap)

    def peek(self):
        self.clean()
        return self.heap[0][2]

    def pop(self):
        self.clean()

        ret = heappop(self.heap)[2]

        self.c[ret] -= 1
        self.sz -= 1
        self.sum -= ret

        return ret

    def remove(self, x):
        self.c[x] -= 1
        self.sz -= 1
        self.sum -= x

    def push(self, x):

        entry = [self.key(x), id(x), x]

        heappush(self.heap, entry)

        self.c[x] += 1
        self.sz += 1
        self.sum += x


class PartitionSum:

    # lowest <= k elements
    def __init__(self, k):
        self.k = k
        self.lo = LazySumHeap(lambda x:-x)
        self.hi = LazySumHeap(lambda x:x)

    def rebalance(self):

        while len(self.lo) > self.k:
            self.hi.push(self.lo.pop())

        while self.hi and len(self.lo) < self.k:
            self.lo.push(self.hi.pop())

    def add(self, x):

        if not self.hi or (self.hi and x < self.hi.peek()):
            self.lo.push(x)
        else:
            self.hi.push(x)

        self.rebalance()

    def remove(self, x):

        if x <= self.lo.peek():
            self.lo.remove(x)
        else:
            self.hi.remove(x)

        self.rebalance()


class MKAverage:

    def __init__(self, M: int, K: int):
        global m,k,q,lo,hi

        m = M
        k = K

        q = deque()

        lo = PartitionSum(k)
        hi = PartitionSum(m-k)


    def addElement(self, x: int) -> None:
        global m,k,q,lo,hi

        if len(q) <= m-1:
            q.append(x)
            lo.add(x)
            hi.add(x)

        else:

            y = q.popleft()
            lo.remove(y)
            hi.remove(y)

            q.append(x)
            lo.add(x)
            hi.add(x)

    def calculateMKAverage(self) -> int:
        global m,k,q,lo,hi

        if len(q) < m:
            return -1
        else:
            return (lo.hi.sum - hi.hi.sum) // (m-k-k)
