from sortedcontainers import SortedList


class MKAverage:

    def __init__(self, m: int, k: int):
        self.m = m
        self.k = k
        self.q = deque()
        self.mid = SortedList()
        self.total = 0

    def addElement(self, num: int) -> None:
        self.q.append(num)
        self.mid.add(num)
        self.total += num
        if len(self.q) > self.m:
            x = self.q.popleft()
            self.mid.remove(x)
            self.total -= x

    def calculateMKAverage(self) -> int:
        if len(self.q) < self.m:
            return -1
        remove = sum(self.mid[:self.k]) + sum(self.mid[-self.k:])
        return int((self.total - remove) / (self.m - self.k * 2))

