        #maintain stream
        self.stream.append(num)

    def calculateMKAverage(self) -> int:
        if len(self.stream)<self.m:
            return -1
        orderlist=self.tree.preOrder(self.root)
        sellist=orderlist[self.k:-self.k]
        return int(sum(sellist)/len(sellist))


