class MKAverage {
 private:
  static constexpr int n_parts = 3;
  static constexpr int part1_i = 0;
  static constexpr int part2_i = 1;
  static constexpr int part3_i = 2;

 public:
  MKAverage(const int m, const int k) : m_(m), k_(k), sums_{} {
  }

  void addElement(const int num) {
    q_.emplace(num);
    insert(num);
    if (q_.size() > m_) {
      remove(q_.front());
      q_.pop();
    }
  }

  int calculateMKAverage() {
    if (q_.size() < m_) {
      return -1;
    }
    return sums_[part2_i] / part_size(part2_i);
  }

 private:
  int part_size(const int i_part) const {
    if (i_part == 0 || i_part == 2) {
      return k_;
    }
    return m_ - 2 * k_;
  }

  void insert(const int num) {
    insert_part(part1_i, num);
    for (int i_part = part1_i;
         i_part + 1 < n_parts && parts_[i_part].size() > part_size(i_part);
         ++i_part) {
      move(i_part, i_part + 1);
    }
  }

  void remove(const int num) {
    for (int i_part = part3_i; i_part > -1; --i_part) {
      if (remove_part(i_part, num)) {
        for (; i_part + 1 < n_parts; ++i_part) {
          move(i_part + 1, i_part);
        }
        break;
      }
    }
  }

  void insert_part(const int i_part, const int num) {
    parts_[i_part].emplace(num);
    sums_[i_part] += num;
  }

  bool remove_part(const int i_part, const int num) {
    auto itr = parts_[i_part].find(num);
    if (itr == parts_[i_part].end()) {
      return false;
    }

    parts_[i_part].erase(itr);
    sums_[i_part] -= num;
    return true;
  }

  void move(const int i_part_from, const int i_part_to) {
    const bool forward = i_part_from < i_part_to;
    multiset<int> &from = parts_[i_part_from];
    multiset<int> &to = parts_[i_part_to];
    const int moved = forward ? *from.rbegin() : *from.begin();
    auto moved_itr = forward ? from.find(moved) : from.begin();
    from.erase(moved_itr);
    sums_[i_part_from] -= moved;
    to.emplace(moved);
    sums_[i_part_to] += moved;
  }

  const int m_;
  const int k_;
  queue<int> q_;
  multiset<int> parts_[n_parts];
  long long sums_[n_parts];
};
