class MKAverage {
 private:
  static constexpr int n_parts = 3;
  static constexpr int part1_i = 0;
  static constexpr int part2_i = 1;
  static constexpr int part3_i = 2;

 public:
  MKAverage(const int m, const int k) : m_(m), k_(k), part2_sum_(0) {
  }

  void addElement(const int num) {
    if (dq_.size() < m_) {
      dq_.emplace_back(num);
      if (dq_.size() == m_) {
        vector<int> nums(dq_.begin(), dq_.end());
        sort(nums.begin(), nums.end());
        parts_[part1_i].insert(nums.begin(), nums.begin() + k_);
        parts_[part2_i].insert(nums.begin() + k_, nums.end() - k_);
        parts_[part3_i].insert(nums.end() - k_, nums.end());
        part2_sum_ = accumulate(nums.begin() + k_, nums.end() - k_, 0);
      }
      return;
    }

    // dq_.size() >= m_
    dq_.emplace_back(num);
    if (num <= *parts_[part1_i].rbegin()) {
      parts_[part1_i].emplace(num);
    } else if (num >= *parts_[part3_i].begin()) {
      parts_[part3_i].emplace(num);
    } else {
      parts_[part2_i].emplace(num);
      part2_sum_ += num;
    }

    const int to_erase = dq_.front();
    dq_.pop_front();
    if (to_erase <= *parts_[part1_i].rbegin()) {
      parts_[part1_i].erase(parts_[part1_i].find(to_erase));
    } else if (to_erase >= *parts_[part3_i].begin()) {
      parts_[part3_i].erase(parts_[part3_i].find(to_erase));
    } else {
      parts_[part2_i].erase(parts_[part2_i].find(to_erase));
      part2_sum_ -= to_erase;
    }

    while (parts_[part1_i].size() > k_) {
      move_forward(part1_i);
    }
    while (parts_[part3_i].size() > k_) {
      move_backward(part3_i);
    }
    while (parts_[part1_i].size() < k_) {
      move_backward(part2_i);
    }
    while (parts_[part3_i].size() < k_) {
      move_forward(part2_i);
    }
  }

  int calculateMKAverage() {
    if (dq_.size() < m_) {
      return -1;
    }

    return part2_sum_ / (m_ - 2 * k_);
  }

 private:
  void move_forward(const int source_part) {
    const int target_part = source_part + 1;
    const int to_move = *parts_[source_part].rbegin();
    update_part2_sum(source_part, target_part, to_move);
    auto itr = prev(parts_[source_part].end());
    auto node = parts_[source_part].extract(itr);
    parts_[target_part].insert(move(node));
  }

  void move_backward(const int source_part) {
    const int target_part = source_part - 1;
    const int to_move = *parts_[source_part].begin();
    update_part2_sum(source_part, target_part, to_move);
    auto node = parts_[source_part].extract(parts_[source_part].begin());
    parts_[target_part].insert(move(node));
  }

  void update_part2_sum(const int source_part, const int target_part, const int to_move) {
    if (source_part == part2_i) {
      part2_sum_ -= to_move;
    } else if (target_part == part2_i) {
      part2_sum_ += to_move;
    }
  }

  const int m_;
  const int k_;
  int part2_sum_;
  deque<int> dq_;
  multiset<int> parts_[n_parts];
};
