type Range struct {
    s   []int
    sum int
}

func (r *Range) insert(x int) {
    i := sort.SearchInts(r.s, x)
    r.s = append(r.s, 0)
    copy(r.s[i+1:], r.s[i:])
    r.s[i] = x
    r.sum += x
}

func (r *Range) remove(x int) {
    i := sort.SearchInts(r.s, x)
    r.s = append(r.s[:i], r.s[i+1:]...)
    r.sum -= x
}

func Constructor(m int, k int) MKAverage {
    return MKAverage{
        m: m,
        k: k,
    }
}

func (mk *MKAverage) AddElement(num int) {
    mk.q = append(mk.q, num)
    n := len(mk.q)
    if n < mk.m {
        return
    }
    if n == mk.m {
        qq := make([]int, mk.m)
        copy(qq, mk.q)
        sort.Ints(qq)
        for i := 0; i < mk.k; i++ {
            mk.L.insert(qq[i])
        }
        for i := mk.k; i < mk.m-mk.k; i++ {
            mk.M.insert(qq[i])
        }
        for i := mk.m - mk.k; i < mk.m; i++ {
            mk.R.insert(qq[i])
        }
    }
    if n > mk.m {
        mk.M.insert(num)
        x := mk.L.s[len(mk.L.s)-1]
        y := mk.M.s[0]
