# Complexity
- Time complexity:
    - MKAverage(int m, int k): $$O(1)$$

    - AddElement(int num):
        - count < m: $$O(1)$$
        - count == m: $$O(k + mlogm)$$
        - count > m: $$O(logm)$$
    - CalculateMKAverage(): $$O(1)$$

- Space complexity: $$O(m)$$

# Code
```
public class MKAverage
{
    private readonly int m;
    private readonly int k;

    private readonly Queue<int> q;
    private readonly List<int> stream;

    private long sum;
    private long s1;
    private long s2;

    public MKAverage(int m, int k)
    {
        this.m = m;
        this.k = k;

        q = new Queue<int>();
        stream = new List<int>();

        sum = 0;
        s1 = 0;
        s2 = 0;
    }

    public void AddElement(int num)
    {
        q.Enqueue(num);

        if (stream.Count < m)
        {
            stream.Add(num);

            if (stream.Count == m)
            {
                stream.Sort();

                for (int i = 0; i < k; i++)
                {
                    s1 += stream[i];
                    s2 += stream[m - 1 - i];
                }
            }
        }
        else
        {
            InsertElement(num);
            DeleteElement();
        }

        sum += num;
    }

    private void InsertElement(int num)
    {
        int index = stream.BinarySearch(num);
        if (index < 0)
        {
            index = ~index;
        }

        stream.Insert(index, num);

        if (index < k)
        {
            s1 -= stream[k];
            s1 += num;
        }
        if (index > m - k)
        {
            s2 -= stream[m - k];
            s2 += num;
        }
    }

    private void DeleteElement()
    {
        int num = q.Dequeue();
        int index = stream.BinarySearch(num);

        if (index < k)
        {
            s1 += stream[k];
            s1 -= num;
        }
        if (index > m - k)
        {
            s2 += stream[m - k];
            s2 -= num;
        }

        stream.RemoveAt(index);
        sum -= num;
    }

    public int CalculateMKAverage()
    {
        if (stream.Count < m)
        {
            return -1;
        }

        return (int)((sum - s1 - s2) / (m - 2 * k));
    }
}
