Modularized to prevent cursor manipulation mistakes.

```
class MKAverage {
    private final int M;
    private final int K;

    private Queue<Integer> queue;
    private TreeMap<Integer, Integer> smallestK;
    private TreeMap<Integer, Integer> largestK;
    private TreeMap<Integer, Integer> middle;
    private int smallestKCount;
    private int largestKCount;
    private int middleSum;

    public MKAverage(int m, int k) {
      this.M = m;
      this.K = k;
      this.queue = new LinkedList<>();
      this.smallestK = new TreeMap<>();
      this.largestK = new TreeMap<>();
      this.middle = new TreeMap<>();
    }

    // O(log n)
    public void addElement(int num) {
      // Maintain size M
      // Remove first, since adding requires rebalancing anyway; safes extra rebalancing during deletion!
      if (queue.size() == M) {
        var evictNum = queue.poll();

        if (smallestK.containsKey(evictNum)) {
          removeFromSmallestPile(evictNum);
        } else if (largestK.containsKey(evictNum)) {
          removeFromLargestPile(evictNum);
        } else {
          removeFromMiddle(evictNum);
        }
      }

      queue.add(num);
      addToSmallestPile(num);

      // smallest -> middle
      // Trade elements, so not smallest=[largest new], middle=[smaller], top=[larger]
      var tradeElem = smallestK.lastKey();
      removeFromSmallestPile(tradeElem);
      addToMiddle(tradeElem);

      // If under sized, get it back from middle; otherwise stays, middle reservoir
      if (smallestKCount < K) {
        var smallest = middle.firstKey();
        removeFromMiddle(smallest);
        addToSmallestPile(smallest);
      }

      // middle -> largest everytime
      // So new largest element can shift from middle to largest pile
      if (!middle.isEmpty()) {
        var largest = middle.lastKey();
        removeFromMiddle(largest);
        addToLargestPile(largest);
      }

      // largest -> middle
      // Put back element to keep largest K size.
      if (largestKCount > K) {
        var smallest = largestK.firstKey();
        removeFromLargestPile(smallest);
        addToMiddle(smallest);
      }
    }

    // O(1)
    public int calculateMKAverage() {
      if (queue.size() < M) {
        return -1;
      }
      return middleSum / (M - smallestKCount - largestKCount);
    }

    private void addToSmallestPile(int num) {
      addElement(smallestK, num);
      smallestKCount++;
    }

    private void addToLargestPile(int num) {
      addElement(largestK, num);
      largestKCount++;
    }

    private void addToMiddle(int num) {
      addElement(middle, num);
      middleSum += num;
    }

    private void removeFromSmallestPile(int evictNum) {
      removeElement(smallestK, evictNum);
      smallestKCount--;
    }

    private void removeFromLargestPile(int evictNum) {
