class MKAverage {
    long[] bit;
    int[] cnt;


    Queue<Integer> q = new LinkedList<>();

    int M, K;

    private void add(int x, int delta) {
        for(; x < 100001; x = (x | (x + 1))) bit[x] += delta;
    }

    private long sum(int x) {
        long res = 0;
        for(; x >= 0; x = (x & (x + 1)) - 1) res += bit[x];
        return res;
    }

    private void add2(int x, int delta) {
        for(; x < 100001; x = (x | (x + 1))) cnt[x] += delta;
    }

    private int sum2(int x) {
        int res = 0;
        for(; x >= 0; x = (x & (x + 1)) - 1) res += cnt[x];
        return res;
    }

    public MKAverage(int m, int k) {
        M = m;
        K = k;
        bit = new long[100001];
        cnt = new int[100001];
    }

    public void addElement(int num) {
        if(q.size() == M) {
            int el = q.poll();
            add(el, -el);
            add2(el, -1);
        }
        q.add(num);
        add(num, num);
        add2(num, 1);
    }

    public int calculateMKAverage() {
        int l = 1, r = 100000, mid = -1;
        if(q.size() < M) return -1;
        if(sum2(l) >= M - K) {
            return 1;
        }
        while(r - l > 1) {
            mid = r - (r - l) / 2;
            if(sum2(mid) >= M - K) {
                r = mid;
            }
            else {
                l = mid;
            }
        }
        int ll = l, rr = r;
        long minus = (long)(sum2(rr) - (M - K)) * rr;
        l = 1; r = 100000; mid = -1;
        if(sum2(l) >= K) {

            return (int)((sum(rr) - K * l - minus) / (M - 2 * K));
        }
        else {
            while(r - l > 1) {
                mid = r - (r - l) / 2;
                if(sum2(mid) >= K) {
                    r = mid;
                }
                else {
                    l = mid;
                }
            }
            long minus2 = (long)(K - sum2(l)) * r + sum(l);
            return (int)((sum(rr) - minus - minus2) / (M - 2 * K));
        }
    }
}

