class Item implements Comparable<Item> {
    private int key;
    private int val;

    public Item(int key, int val){
        this.key = key;
        this.val = val;
    }

    public int getKey(){
        return key;
    }

    public int getValue(){
        return val;
    }
    @Override 
    public int compareTo(Item other){
        if (this.getKey() == other.getKey()) return this.getValue()-other.getValue();
        else return this.getKey() - other.getKey();
    }
    @Override
    public int hashCode() {
        return key*Integer.MAX_VALUE + val;

    }
}
class MKAverage {
    int k;
    int m;
    int i;
    int sum;

    TreeSet<Item> kSmallest = new TreeSet();
    TreeSet<Item> kLargest = new TreeSet();
    TreeSet<Item> q = new TreeSet();
    Map<Integer, Item> map = new HashMap();

    public MKAverage(int m, int k) {
        this.m = m;
        this.k = k;
    }

    public void addElement(int num) {
        i++;
        Item item = new Item(num, i);
        map.put(i, item);

        if(i > m){
            Item oldestItem = map.remove(i-m);
            if (kLargest.contains(oldestItem)) kLargest.remove(oldestItem);
            if (kSmallest.contains(oldestItem)) kSmallest.remove(oldestItem);
            if (q.contains(oldestItem)) {
                q.remove(oldestItem);
                sum -= oldestItem.getKey();
            }
        }
        addElement(item);
    }

    public int calculateMKAverage() {
        if (i < m) return -1;
        if (q.size() == 0) return 0;
        return sum/q.size();
    }

    private void addElement(Item item){
        q.add(item);
        sum += item.getKey();
        Item largest = null, smallest = null, p1 = null, p2 = null;

        largest = q.pollLast();
        sum -= largest.getKey();

        if(q.size() == 0) smallest = largest;
        else {
            smallest = q.pollFirst();
            sum -= smallest.getKey();
        }
        kLargest.add(largest);
        kSmallest.add(smallest);

        if (kLargest.size() > k) p1 = kLargest.pollFirst();
        if (kSmallest.size() > k) p2 = kSmallest.pollLast();

        if(p1 != null && !kSmallest.contains(p1)) {
            q.add(p1);
            sum += p1.getKey();
        }

        if(p2 != null && !kLargest.contains(p2) && p1 != p2) {
            q.add(p2);
            sum += p2.getKey();
        }
    }
}

