class MKAverage {
    int sum;
    int m;
    int k;
    Deque<Integer> queue;
    TreeMap<Integer, Integer> streamOrder;
    int total;

    public MKAverage(int m, int k) {
        streamOrder = new TreeMap<>();
        queue = new ArrayDeque();
        this.m = m;
        this.k = k;
        this.sum = 0;
        this.total = 0;
    }

    public void addElement(int num) {
        sum += num;
        queue.addLast(num);
        total++;
        streamOrder.put(num, streamOrder.getOrDefault(num, 0) + 1);

        if (total > m) {
            int last = queue.pollFirst();
            sum -= last;
            total--;
            streamOrder.put(last, streamOrder.get(last) - 1);

            if (streamOrder.get(last) == 0) {
                streamOrder.remove(last);
            }
        }
    }

    public int calculateMKAverage() {
        if (k > m || total < m) {
            return -1;
        }

        int toRemoveCount = k;
        int curTotal = sum;

        for (Map.Entry<Integer, Integer> entry : streamOrder.entrySet()) {
            int curRemoveCount = Math.min(entry.getValue(), toRemoveCount);
            curTotal -= curRemoveCount * entry.getKey();
            toRemoveCount -= curRemoveCount;

            if (toRemoveCount == 0)
                break;
        }

        toRemoveCount = k;

        for (Map.Entry<Integer, Integer> entry : streamOrder.descendingMap().entrySet()) {
            int curRemoveCount = Math.min(entry.getValue(), toRemoveCount);
            curTotal -= curRemoveCount * entry.getKey();
            toRemoveCount -= curRemoveCount;

            if (toRemoveCount == 0)
                break;
        }

        return curTotal/(total - 2 * k);
    }
}

