```
class MKAverage {
public:
    const int M, K;

    queue<int> last;
    multiset<int> smallest;
    multiset<int> middle;
    multiset<int> largest;
    long long S;


    MKAverage(int m, int k) : M(m), K(k), S(0) {

    }

    void balance() {
        while (largest.size() > K) {
            int val = *largest.begin();
            largest.erase(largest.begin());
            middle.insert(val);
            S += val;
        }
        while (smallest.size() > K) {
            auto it = prev(smallest.end());
            int val = *it;
            smallest.erase(it);
            middle.insert(val);
            S += val;
        }        
        while (smallest.size() < K) {
            int val = *middle.begin();
            middle.erase(middle.begin());
            S -= val;
            smallest.insert(val);
        }
        while (largest.size() < K) {
            int val = *prev(middle.end());
            middle.erase(prev(middle.end()));
            S -= val;
            largest.insert(val);
        }
    }

    void removeElement(int num) {
        if (auto it = largest.find(num); it != largest.end())
            largest.erase(it);
        else if (auto it = middle.find(num); it != middle.end()) 
            middle.erase(it), S -= num;
         else if (auto it = smallest.find(num); it != smallest.end())
             smallest.erase(it);
    }

    void addElement(int num) {
        last.push(num);

        if (largest.empty() || num >= *largest.begin()) {
            largest.insert(num);
        } else if (middle.empty() || num >= *middle.begin()) {
            middle.insert(num);
            S += num;
        } else
            smallest.insert(num);

        if (last.size() > M) {
            int rval = last.front();
            last.pop();
            removeElement(rval);
        }

        if (last.size() >= M)
            balance();
    }

    int calculateMKAverage() {
        return last.size() < M ? -1 : S/middle.size();
    }
};
