```
class MKAverage {
    int m;
    int k;
    int sum;
    int count;
    TreeMap<Integer, Integer> treeMap;
    int left; // value of kth smallest num 
    int leftPtr; // count of `left` equivalent nums in the k smallest nums 
    int right;
    int rightPtr;
    Deque<Integer> dq;

    public MKAverage(int m, int k) {
        this.m=m;
        this.k=k;
        this.sum=0;
        this.count=0;
        treeMap = new TreeMap<>();
        left=0;
        leftPtr=k;
        right=0;
        rightPtr=k;
        dq=new LinkedList<>();
    }

    public void addElement(int num) {
        if(count<m){
            sum+=num;
            treeMap.put(num, treeMap.getOrDefault(num, 0)+1);
            dq.add(num);
            count++;

            if(count==m){
                for(Map.Entry<Integer, Integer> entry: treeMap.entrySet()){
                    if(leftPtr-entry.getValue()<=0){
                        left = entry.getKey();
                        sum-=entry.getKey()*leftPtr;
                        break;
                    } else {
                        leftPtr-=entry.getValue();
                        sum-=entry.getKey()*entry.getValue();
                    }
                }

                for(Map.Entry<Integer, Integer> entry: treeMap.descendingMap().entrySet()){
                    if(rightPtr-entry.getValue()<=0){
                        right = entry.getKey();
                        sum-=entry.getKey()*rightPtr;
                        break;
                    } else {
                        rightPtr-=entry.getValue();
                        sum-=entry.getKey()*entry.getValue();
                    }
                }
            }
        } else {
            treeMap.put(num, treeMap.getOrDefault(num, 0)+1);
            dq.add(num);
            if(num<left){
                leftPtr--;
                sum+=left;
                if(leftPtr==0){
                    left=treeMap.lowerKey(left);
                    leftPtr=treeMap.get(left);
                }
            }else if(num>right){
                rightPtr--;
                sum+=right;
                if(rightPtr==0){
                    right=treeMap.higherKey(right);
                    rightPtr=treeMap.get(right);
                }
            } else {
                sum+=num;
            }

            int prevNum = dq.pollFirst();
            if(treeMap.get(prevNum)==1) treeMap.remove(prevNum);
            else treeMap.put(prevNum, treeMap.get(prevNum)-1);

            if(prevNum==left){
                if(!treeMap.containsKey(prevNum)||treeMap.get(left)<leftPtr){
                    left=treeMap.higherKey(left);
                    leftPtr=1;
                }
                sum-=left;
            } else if(prevNum<left){
                leftPtr++;
                if(treeMap.get(left)<leftPtr){
                    left=treeMap.higherKey(left);
                    leftPtr=1;
                }
                sum-=left;
            } else if(prevNum==right){
                if(!treeMap.containsKey(prevNum)||treeMap.get(right)<leftPtr){
                    right=treeMap.lowerKey(right);
                    rightPtr=1;
                }
                sum-=right;
            } else if(prevNum>right){
                rightPtr++;
                if(treeMap.get(right)<rightPtr){
                    right=treeMap.lowerKey(right);
                    rightPtr=1;
                }
                sum-=right;
            } else {
                sum-=prevNum;
            }
        }

    }

    public int calculateMKAverage() {
        if(count<m)return -1;
        return sum/(m-2*k);
    }
}

/**
 * Your MKAverage object will be instantiated and called as such:
 * MKAverage obj = new MKAverage(m, k);
 * obj.addElement(num);
 * int param_2 = obj.calculateMKAverage();
 */
```

